  conferenceLeaderColor,
  divisionLeaderColor,
  divisionRankedColor,
  leagueLeaderColor,
  outOfPlayoffsColor,
  wildCardColor,
} from "../../../style/LandingPage/colors";

interface LandingPageStandingTableProps {
  standingsData: StandingsTeam[];
}
export default function LandingPageStandingsTable({
  standingsData,
}: LandingPageStandingTableProps) {
  return (
    <Table data={standingsData}>
      {(standingsList:StandingsTeam[]) => (
        <>
          <Header>
            <HeaderRow>
              <HeaderCell>#</HeaderCell>
              <HeaderCell>Team</HeaderCell>
              <HeaderCell>W</HeaderCell>
              <HeaderCell>L</HeaderCell>
              <HeaderCell>OT</HeaderCell>
              <HeaderCell>Record</HeaderCell>
              <HeaderCell>P</HeaderCell>
              <HeaderCell>P%</HeaderCell>
            </HeaderRow>
          </Header>
          <Body>
            {standingsList.map((team:StandingsTeam) => (
                <Row key={team.id} item={team}>
                    <Cell>{team.conferenceStandingsPlace}</Cell>
                    <Cell>{team.teamName}</Cell>
                    <Cell>{team.wins}</Cell>
                    <Cell>{team.losses}</Cell>
                    <Cell>{team.otLosses}</Cell>
                    <Cell>{team.record}</Cell>
                    <Cell>{team.points}</Cell>
                    <Cell>{team.pointsPercentage}</Cell>
                </Row>
            ))}
          </Body>
        </>
      )}
    </Table>
  );
