import { Col, Row } from "react-bootstrap";
import React from "react";
import { useEffect, useState } from "react";
import { StandingsTeam } from "../../../Data/Models/StandingsTeam";

interface LandingPageStandingsRowProps{
  team:StandingsTeam,
}
export default function LandingPageStandingsRow({team}:LandingPageStandingsRowProps){
    const [bottomBorder, setBottomBorder] = useState("none");
    const [leagueRank, setLeagueRank] = useState<number>(0);
    const [conferenceRank, setConferenceRank] = useState<number>(0);
    const [divisionRank, setDivisionRank] = useState<number>(0)
    const [inPlayoffs, setInPlayoffs] = useState<boolean>(false);
    const [rowColor, setRowColor] = useState<string>("");

