import { Col, Row } from "react-bootstrap";
import "../../style/LandingPage/LandingPageRow.css";
import RowBlock from "./RowBlock";
import React from "react";
import { StandingsTeam } from "../../Data/Models/StandingsTeam";
import { PlayerStatLeader } from "../../Data/Models/PlayerStatLeader";

interface PlayerStatLeaderProps {
  title: string;
  data: PlayerStatLeader[];
}
export default function PlayerStatLeaderRow({
  title,
  data,
}: PlayerStatLeaderProps) {
  if (data.length > 1) {
    return (
      <div>
        <Row>
          <Col className="landing-header">
            <h2>{title}</h2>
          </Col>
        </Row>
        <Row className="row-scroller">
          {data.map((player: PlayerStatLeader) => (
            <Col md={4} className="row-scroller-column" key={player.id}>
              <div className="stat-leader-block">
                <div className="block-picture">
                  <img src={player.playerImage} alt=""></img>
                </div>
                <div className="block-info">
                  <span className="block-info-name">
                    {player.firstName + " " + player.lastName}
                  </span>
                  <p className="block-info-info">Goal Leader</p>
                </div>
                <div className="block-info">
                  <h2>{player.value}</h2>
                </div>
                <div className="clear-div"></div>
              </div>
            </Col>
          ))}
        </Row>
      </div>
    );
  }
  return <></>;
}
