import { Col, Container, Row } from "react-bootstrap";
import QuickLinks from "../Components/LandingPage/QuickLinks";
import LandingPageRow from "../Components/LandingPage/LandingPageRow";
import LandingPageStandings from "../Components/LandingPage/LandingPageStandings/LandingPageStandings";
import {
  individualLeadersData,
  playerAwards,
  teamAwards,
} from "../Data/LocalData/LandingPageLocalData";
import { useEffect, useState} from "react";
import { CreateDraftLotteryOddsArray } from "../Data/Helpers/DraftLotteryOddsHelper";
import React from "react";
import { GetCurrentStandings, GetStatLeaders } from "../Services/ApiHandler";
import { DraftLotteryTeam } from "../Data/Models/DraftLotteryTeam";
import { FunctionInterpolation } from "@emotion/react";
import { PlayerStatLeader } from "../Data/Models/PlayerStatLeader";
import PlayerStatLeaderConverter from "../Data/Helpers/PlayerStatLeaderConverter";

export default function LandingPage(){
  //use States that will set with data that will be passed to components in landingPage
  const [draftLotteryOddsData, setDraftLotteryOddsData] = useState([]);
  const [goalLeaderData, setGoalLeaderData] = useState([]);
  const [assistLeaderData, setAssistLeaderData] = useState([]);
  const [pointsLeaderData, setPointsLeaderData] = useState([]);

  useEffect(()=>{
    GetDraftLotteryOdds(setDraftLotteryOddsData);
    GetPlayerStatLeaders(setGoalLeaderData, setAssistLeaderData, setPointsLeaderData);
  },[])
  return (
    <Container fluid>
      <Row>
        <Col md={7}>
          <QuickLinks></QuickLinks>
          <LandingPageRow
            title={"Individual Leaders"}
            data={individualLeadersData}
          ></LandingPageRow>
          <LandingPageRow
            title={"Draft Lottery Odds"}
            data={draftLotteryOddsData}
          ></LandingPageRow>
          <LandingPageRow
            title={"Player Awards (2022)"}
            data={playerAwards}
          ></LandingPageRow>
          <LandingPageRow
            title={"Team Awards (2022)"}
            data={teamAwards}
          ></LandingPageRow>
        </Col>
        <Col md={5}>
          <LandingPageStandings></LandingPageStandings>
        </Col>
      </Row>
    </Container>
  );
};
  //Custom hook that parses and modifies the api data to create draftLotteryOddsArray (which is then set in the useState)
  function GetDraftLotteryOdds(setDraftLotteryOddsData:Function) {
    GetCurrentStandings()
      .then((response) => {
        const responseData = response.data.standings;
        const draftLotteryOddsArray:DraftLotteryTeam[] = CreateDraftLotteryOddsArray(
          responseData
        );
        setDraftLotteryOddsData(draftLotteryOddsArray);
      })
      .catch((error) => console.log(error));
  }
  function GetPlayerStatLeaders(setGoalLeaderData:Function, setAssistLeaderData:Function, setPointsLeaderData:Function){
     GetStatLeaders("goals").then((response) =>{
        const responseData = response.data;
        const goalStatLeaders:PlayerStatLeader[] = PlayerStatLeaderConverter(responseData, "goals");
        setGoalLeaderData(goalStatLeaders);
     })
     .catch((error) => console.log(error));
     GetStatLeaders("assists").then((response) =>{
      const responseData = response.data;
      const assistLeaderData:PlayerStatLeader[] = PlayerStatLeaderConverter(responseData, "assists");
      setAssistLeaderData(assistLeaderData);
   })
   .catch((error) => console.log(error));
   GetStatLeaders("points").then((response) =>{
    const responseData = response.data;
    const pointsLeaderData:PlayerStatLeader[] = PlayerStatLeaderConverter(responseData, "points");
    setPointsLeaderData(pointsLeaderData);
 })
 .catch((error) => console.log(error));
  }
