import { useTheme } from "@table-library/react-table-library/theme";
import { getTheme } from "@table-library/react-table-library/baseline";
import { StandingsTeam } from "../../../Data/Models/StandingsTeam";
import {
  Column,
  CompactTable,
} from "@table-library/react-table-library/compact";
import * as React from "react";
import { TableNode } from "@table-library/react-table-library";
import { Table } from "react-bootstrap";
import { conferenceLeaderColor, divisionLeaderColor, divisionRankedColor, leagueLeaderColor, outOfPlayoffsColor, wildCardColor } from "../../../style/LandingPage/colors";

interface LandingPageStandingTableProps {
  standingsData: StandingsTeam[];
}
export default function LandingPageStandingsTable({
  standingsData,
}: LandingPageStandingTableProps) {
  // const theme = useTheme(getTheme());
  // const COLUMNS= [
  //     {label: '#', renderCell:(item:StandingsTeam)=>item.conferenceStandingsPlace},
  //     {label: 'Team', renderCell:(item:StandingsTeam)=>item.teamName},
  //     {label: 'W', renderCell:(item:StandingsTeam)=>item.wins},
  //     {label: 'L', renderCell:(item:StandingsTeam)=>item.losses},
  //     {label: 'OT', renderCell:(item:StandingsTeam)=> item.otLosses},
  //     {label: 'Record', renderCell:(item:StandingsTeam)=>item.record},
  //     {label:'P', renderCell:(item:StandingsTeam)=>item.points},
  //     {label:'P%', renderCell:(item:StandingsTeam)=>item.pointsPercentage},
  // ];
  // return (
  //     <CompactTable columns={COLUMNS} data={standingsData} theme={theme}/>
  // )
  return (
    <Table responsive="sm" striped bordered>
      <thead>
        <tr>
          <th>#</th>
          <th>Team</th>
          <th>W</th>
          <th>L</th>
          <th>OT</th>
          <th>Record</th>
          <th>P</th>
          <th>P%</th>
        </tr>
      </thead>
      <tbody>
        {standingsData.map((team: StandingsTeam) => {
          let rowColor: string = "#ffccff"
          if (team.leagueStanding === 1) {
            rowColor = leagueLeaderColor;
          } else if (team.conferenceStandingsPlace === 1) {
            rowColor = conferenceLeaderColor;
          } else if (team.divisionStandingsPlace === 1) {
            rowColor = divisionLeaderColor;
          } else if (team.divisionStandingsPlace <= 3) {
            rowColor = divisionRankedColor;
          } else if (team.wildCardRank === 1 || team.wildCardRank === 2) {
            rowColor = wildCardColor
          } else {
            rowColor = outOfPlayoffsColor;
          }
          return (
            <tr style={{backgroundColor:rowColor}}>
              <td>{team.conferenceStandingsPlace}</td>
              <td>{team.teamName}</td>
              <td>{team.wins}</td>
              <td>{team.losses}</td>
              <td>{team.otLosses}</td>
              <td>{team.record}</td>
              <td>{team.points} pts</td>
              <td>{team.pointsPercentage}%</td>
            </tr>
          );
        })}
      </tbody>
    </Table>
  );
}
