import { Container } from "react-bootstrap";
import { useState, useEffect, useRef, MutableRefObject } from "react";
import StandingsContainer from "./StandingsContainer";
import { GetCurrentStandings } from "../../../Services/ApiHandler";
import { CreateConferenceStandingsArray } from "../../../Data/Helpers/ConferenceStandingsHelper";
import React from "react";
import { StandingsTeam } from "../../../Data/Models/StandingsTeam";

export default function LandingPageStandings(){
  const [easternStandingsData, setEasternStandingsData] = useState<StandingsTeam[]>(new Array(16));
  const [westernStandingsData,setWesternStandingsData] = useState<StandingsTeam[]>(new Array(16));

  const [metroStandings, setMetroStandings] = useState<StandingsTeam[]>(new Array(16));
  const [atlanticStandings, setAtlanticStandings] = useState<StandingsTeam[]>(new Array(16));
  const [centralStandings, setCentralStandings] = useState<StandingsTeam[]>(new Array(16));
  const [pacificStandings, setPacificStandings] = useState<StandingsTeam[]>(new Array(16));

  //This useEffect will call apis to get data that will be used in components
  useEffect(() => {
    GetStandings(setEasternStandingsData, setWesternStandingsData);
  }, []);
  useEffect(()=>{
    GetDivisionStandings(setMetroStandings, "Metropolitan", easternStandingsData)
    GetDivisionStandings(setAtlanticStandings, "Atlantic", easternStandingsData)
    GetDivisionStandings(setCentralStandings, "Central", westernStandingsData)
    GetDivisionStandings(setPacificStandings, "Pacific", westernStandingsData)
  },[easternStandingsData, westernStandingsData]);
  return (
    <Container>
      <div>
        <StandingsContainer standingsName="Metropolitan" standingsData={metroStandings}/>
      </div>
      <div>
        <StandingsContainer standingsName="Atlantic" standingsData={atlanticStandings}/>
      </div>
    </Container>
  );
};
function GetStandings(setEasternStandings:Function, setWesternStandings:Function) {
  GetCurrentStandings()
    .then((response) => {
      const responseStandings = response.data.standings;
      const easternStandings = CreateConferenceStandingsArray(
        responseStandings,
        "Eastern"
      );
      const westernStandings = CreateConferenceStandingsArray(
        responseStandings,
        "Western"
      );

      setEasternStandings(easternStandings);
      setWesternStandings(westernStandings);
    })
    .catch((error) => console.log(error));
}
function GetDivisionStandings(setDivisionStandings:Function, divisionName:string, conferenceStandings:StandingsTeam[]){
  let divisionStandingsArray:StandingsTeam[] = new Array(8);
  let i = 0;
  let currentSize = 0;
  do{
    const team:StandingsTeam = conferenceStandings[i];
    if(team.divisionName === divisionName){
      divisionStandingsArray[currentSize] = team;
      currentSize++;
    }
    i++;
  }while(currentSize < 8);
  setDivisionStandings(divisionStandingsArray);
}
